mapper.select:
select cart_id, product_id, user_id, quantity, delete_status
from t_carts
where delete_status = 0
  and user_id = #{userId}
  and product_id=#{productId}

mapper.update:
update t_carts
set quantity=#{quantity}
where delete_status = 0
  and cart_id = #{cartId};

update t_carts
set delete_status = 1 where cart_id in

    #{cartId}
